class Observable {private String myValue;private ArrayList<Observer> allObserver = new ArrayList<Observer>();public void broadcast()  { for (Observer o : allObserver) o.update(this); }public String getValue() { return myValue; }public void setValue(String aValue) { 	myValue = aValue;	this.broadcast(); }public void add(Observer aObserver) { allObserver.add(aObserver); }} // Observable