public class Rechteck {    private double laenge;    private double breite;    public Rechteck(double laenge, double breite) {   	this.breite = breite;  this.laenge = laenge;    }    public double getLaenge() 	{ return laenge; 	}    public double getBreite() 	{ return breite; 	}    public double getUmfang() { return 2 * laenge + 2 * breite; }    public double getDiagonale() {         return Math.sqrt(laenge * laenge + breite * breite);    }}